import{_ as o,c as t,o as i,ah as a}from"./chunks/framework.C2OYgiIb.js";const n="/images1/employees/config_activity.png",l="/images1/employees/general_activity.png",r="/images1/employees/config_working.png",s="/images1/employees/general_working.png",g="/images1/employees/config_departure.png",m="/images1/employees/general_departure.png",p="/images1/employees/config_tags.png",c="/images1/employees/general_tags.png",u="/images1/employees/config_work.png",d="/images1/employees/general_work.png",h="/images1/employees/config_skill.png",y="/images1/employees/general_skill.png",k="/images1/employees/general_add_skill.png",b="/images1/employees/general_add_skill2.png",f="/images1/employees/general_add_skill3.png",C="/images1/employees/config_employment.png",_="/images1/employees/general_employment.png",w="/images1/employees/config_jb.png",q="/images1/employees/general_jb.png",W=JSON.parse('{"title":"Configuration","description":"","frontmatter":{},"headers":[],"relativePath":"master/employees/configurations.md","filePath":"master/employees/configurations.md","lastUpdated":1763638830000}'),v={name:"master/employees/configurations.md"};function T(P,e,S,A,E,x){return i(),t("div",null,e[0]||(e[0]=[a('<h1 id="configuration" tabindex="-1">Configuration <a class="header-anchor" href="#configuration" aria-label="Permalink to &quot;Configuration&quot;">​</a></h1><p><strong>Configurations</strong> are essential for customizing how employee records, work schedules, job roles, and skills are created and managed. These settings are used across employee profiles, job positions, and during recruitment or performance tracking.</p><h2 id="activity-plans" tabindex="-1">Activity Plans <a class="header-anchor" href="#activity-plans" aria-label="Permalink to &quot;Activity Plans&quot;">​</a></h2><blockquote><p><strong>Why it&#39;s used:</strong> Activity Plans are predefined sets of tasks or interactions assigned to employees within specific departments or companies. They streamline repetitive actions like onboarding steps or periodic evaluations.</p></blockquote><h3 id="activity-plan-creation" tabindex="-1">Activity Plan Creation <a class="header-anchor" href="#activity-plan-creation" aria-label="Permalink to &quot;Activity Plan Creation&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Employees → Configurations → Activity Plans → New Activity Plan</code></strong> as shown below.</p><p><img src="'+n+'" alt="Config. Activity"></p></li><li><p>Fill in the following fields:</p><ul><li><strong><em>Name:</em></strong> A title for the activity plan (e.g., Onboarding Plan, Monthly Check-In).</li><li><strong><em>Department:</em></strong> Select or create the department this plan applies to.</li><li><strong><em>Company:</em></strong> Select or create the relevant company.</li><li><strong><em>Status:</em></strong> Enable or disable the plan.</li></ul><p><img src="'+l+'" alt="General Activity"></p></li><li><p>Action Buttons:</p><ul><li>Click <strong><em>Create:</em></strong> to save the activity plan.</li><li>Click <strong><em>Create &amp; Create Another:</em></strong> to save and start a new activity plan.</li><li>Click <strong><em>Cancel</em></strong> to discard changes.</li></ul></li></ol><h2 id="working-schedules" tabindex="-1">Working Schedules <a class="header-anchor" href="#working-schedules" aria-label="Permalink to &quot;Working Schedules&quot;">​</a></h2><blockquote><p><strong>Why it&#39;s used:</strong> Working Schedules define employee work hours, days, and flexibility options. Useful for managing attendance, timesheets, and compliance.</p></blockquote><h3 id="working-schedule-creation" tabindex="-1">Working Schedule Creation <a class="header-anchor" href="#working-schedule-creation" aria-label="Permalink to &quot;Working Schedule Creation&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Employees → Configurations → Working Schedules → New Working Schedule</code></strong> as shown below.</p><p><img src="'+r+'" alt="Config. working"></p></li><li><p>Fill in:</p><h4 id="general-information" tabindex="-1">General Information <a class="header-anchor" href="#general-information" aria-label="Permalink to &quot;General Information&quot;">​</a></h4><ul><li><strong><em>Schedule Name:</em></strong> A name for the schedule (e.g., Standard 9-5, Night Shift).</li><li><strong><em>Timezone:</em></strong> Select the time zone where this schedule applies.</li><li><strong><em>Company:</em></strong> Assign to a specific company.</li></ul><h4 id="work-hours-configuration" tabindex="-1">Work Hours Configuration <a class="header-anchor" href="#work-hours-configuration" aria-label="Permalink to &quot;Work Hours Configuration&quot;">​</a></h4><ul><li><strong><em>Hours Per Day:</em></strong> Define daily working hours.</li><li><strong><em>Full Time Required Hours:</em></strong> Weekly hours required for full-time status.</li></ul><h4 id="flexibility-options" tabindex="-1">Flexibility Options <a class="header-anchor" href="#flexibility-options" aria-label="Permalink to &quot;Flexibility Options&quot;">​</a></h4><ul><li><strong><em>Status:</em></strong> Activate the schedule.</li><li><strong><em>Two Weeks Calendar:</em></strong> Enable alternating week schedules.</li><li><strong><em>Flexible Hours:</em></strong> Allow employees to work flexible hours.</li></ul><p><img src="'+s+'" alt="General Working"></p></li><li><p>Action Buttons:</p><ul><li>Click <strong><em>Create:</em></strong> to save the working schedule.</li><li>Click <strong><em>Create &amp; Create Another:</em></strong> to save and start a new working schedule.</li><li>Click <strong><em>Cancel</em></strong> to discard changes.</li></ul></li></ol><h2 id="departure-reasons" tabindex="-1">Departure Reasons <a class="header-anchor" href="#departure-reasons" aria-label="Permalink to &quot;Departure Reasons&quot;">​</a></h2><blockquote><p><strong>Why it&#39;s used:</strong> Departure Reasons are recorded when an employee leaves the company, helping with exit analysis and compliance.</p></blockquote><h3 id="departure-reason-creation" tabindex="-1">Departure Reason Creation <a class="header-anchor" href="#departure-reason-creation" aria-label="Permalink to &quot;Departure Reason Creation&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Employees → Configurations → Departure Reasons → New Departure Reason</code></strong> as shown below.</p><p><img src="'+g+'" alt="Config. Departure"></p></li><li><p>Enter <strong>Name</strong> (e.g., Resignation, Retirement, Termination).</p><p><img src="'+m+'" alt="General Departure"></p></li><li><p>Action Buttons:</p><ul><li>Click <strong><em>Create:</em></strong> to save the departure reason.</li><li>Click <strong><em>Create &amp; Create Another:</em></strong> to save and start a new departure reason.</li><li>Click <strong><em>Cancel</em></strong> to discard changes.</li></ul></li></ol><h2 id="tags" tabindex="-1">Tags <a class="header-anchor" href="#tags" aria-label="Permalink to &quot;Tags&quot;">​</a></h2><blockquote><p><strong>Why it&#39;s used:</strong> Tags help categorize employees for quick filtering (e.g., Remote, Full-Time, Manager).</p></blockquote><h3 id="tag-creation" tabindex="-1">Tag Creation <a class="header-anchor" href="#tag-creation" aria-label="Permalink to &quot;Tag Creation&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Employees → Configurations → Tags → New Tag</code></strong> as shown below.</p><p><img src="'+p+'" alt="Config. Tags"></p></li><li><p>Enter:</p><ul><li><strong>Name:</strong> Tag label.</li><li><strong>Color:</strong> Assign a color to visually distinguish the tag.</li></ul><p><img src="'+c+'" alt="General Tags"></p></li><li><p>Action Buttons:</p><ul><li>Click <strong><em>Create:</em></strong> to save the tag.</li><li>Click <strong><em>Create &amp; Create Another:</em></strong> to save and start a new tag.</li><li>Click <strong><em>Cancel</em></strong> to discard changes.</li></ul></li></ol><h2 id="work-locations" tabindex="-1">Work Locations <a class="header-anchor" href="#work-locations" aria-label="Permalink to &quot;Work Locations&quot;">​</a></h2><blockquote><p><strong>Why it&#39;s used:</strong> Work Locations specify where employees perform their duties, supporting location-based reporting and resource planning.</p></blockquote><h3 id="work-location-creation" tabindex="-1">Work Location Creation <a class="header-anchor" href="#work-location-creation" aria-label="Permalink to &quot;Work Location Creation&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Employees → Configurations → Work Locations → New Work Location</code></strong> as shown below.</p><p><img src="'+u+'" alt="Config. Work"></p></li><li><p>Enter:</p><ul><li><strong><em>Name:</em></strong> Location title (e.g., Head Office, Remote).</li><li><strong><em>Location Type:</em></strong> Choose <strong>Home</strong>, <strong>Office</strong>, or <strong>Other</strong>.</li><li><strong><em>Location Number:</em></strong> Internal location reference code.</li><li><strong><em>Company:</em></strong> Select the associated company.</li><li><strong><em>Status:</em></strong> Enable/disable the location.</li></ul><p><img src="'+d+'" alt="General Work"></p></li><li><p>Action Buttons:</p><ul><li>Click <strong><em>Create:</em></strong> to save the work location.</li><li>Click <strong><em>Create &amp; Create Another:</em></strong> to save and start a new work location.</li><li>Click <strong><em>Cancel</em></strong> to discard changes.</li></ul></li></ol><h2 id="skill-types" tabindex="-1">Skill Types <a class="header-anchor" href="#skill-types" aria-label="Permalink to &quot;Skill Types&quot;">​</a></h2><blockquote><p><strong>Why it&#39;s used:</strong> Skill Types group various skills to track employee expertise. For example, &quot;Programming&quot; could be a type with skills like Python, Java.</p></blockquote><h3 id="skill-type-creation" tabindex="-1">Skill Type Creation <a class="header-anchor" href="#skill-type-creation" aria-label="Permalink to &quot;Skill Type Creation&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Employees → Configurations → Skill Types → New Skill Type</code></strong> as shown below.</p><p><img src="'+h+'" alt="Config. Skill"></p></li><li><p>Enter:</p><ul><li><strong><em>Name:</em></strong> Skill type (e.g., Programming).</li><li><strong><em>Color:</em></strong> For easy identification.</li><li><strong><em>Status (Toggle):</em></strong> Enable/disable this type.</li></ul><p><img src="'+y+'" alt="General Skill"></p></li><li><p>Action Buttons:</p><ul><li>Click <strong><em>Create:</em></strong> to save the skill type.</li><li>Click <strong><em>Create &amp; Create Another:</em></strong> to save and start a new skill type.</li><li>Click <strong><em>Cancel</em></strong> to discard changes.</li></ul></li></ol><h3 id="add-skills-to-skill-type" tabindex="-1">Add Skills to Skill Type <a class="header-anchor" href="#add-skills-to-skill-type" aria-label="Permalink to &quot;Add Skills to Skill Type&quot;">​</a></h3><ul><li><p>On the <strong>Edit Skill Type</strong> page: <img src="'+k+'" alt="General Add Skill"></p><ul><li><p>Click <strong>New Skill</strong> &gt; enter <strong>Name</strong> &gt; <strong>Create</strong>. <img src="'+b+'" alt="General Add Skill"></p></li><li><p>Click <strong>New Skill Level</strong> &gt; fill in:</p><ul><li><strong>Name</strong></li><li><strong>Level</strong> (e.g., Beginner, Expert)</li><li><strong>Default Level (Toggle)</strong></li><li>Click <strong>Create</strong>.</li></ul><p><img src="'+f+'" alt="General Add Skill"></p></li></ul></li></ul><h2 id="employment-types" tabindex="-1">Employment Types <a class="header-anchor" href="#employment-types" aria-label="Permalink to &quot;Employment Types&quot;">​</a></h2><blockquote><p><strong>Why it&#39;s used:</strong> Employment Types categorize job nature (e.g., Full-Time, Part-Time, Internship), aiding in contract management and reporting.</p></blockquote><h3 id="employment-type-creation" tabindex="-1">Employment Type Creation <a class="header-anchor" href="#employment-type-creation" aria-label="Permalink to &quot;Employment Type Creation&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Employees → Configurations → Employment Types → New Employment Type</code></strong> as shown below.</p><p><img src="'+C+'" alt="Config. Employment"></p></li><li><p>Enter:</p><ul><li><strong><em>Employment Type:</em></strong> Label (e.g., Full-Time).</li><li><strong><em>Country:</em></strong> Applicable region.</li></ul><p><img src="'+_+'" alt="General Employment"></p></li><li><p>Action Buttons:</p><ul><li>Click <strong><em>Create:</em></strong> to save the employment type.</li><li>Click <strong><em>Create &amp; Create Another:</em></strong> to save and start a new employment type.</li><li>Click <strong><em>Cancel</em></strong> to discard changes.</li></ul></li></ol><h2 id="job-positions" tabindex="-1">Job Positions <a class="header-anchor" href="#job-positions" aria-label="Permalink to &quot;Job Positions&quot;">​</a></h2><blockquote><p><strong>Why it&#39;s used:</strong> Job Positions represent roles within the organization (e.g., Software Developer, HR Manager). These are used in employee and recruitment modules.</p></blockquote><h3 id="job-position-creation" tabindex="-1">Job Position Creation <a class="header-anchor" href="#job-position-creation" aria-label="Permalink to &quot;Job Position Creation&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Employees → Configurations → Job Positions → New Job Position</code></strong> as shown below.</p><p><img src="'+w+'" alt="Config. jb"></p></li><li><p>Fill in:</p><h3 id="employment-information" tabindex="-1">Employment Information <a class="header-anchor" href="#employment-information" aria-label="Permalink to &quot;Employment Information&quot;">​</a></h3><ul><li><p><strong><em>Job Position Title:</em></strong> Name of the role.</p></li><li><p><strong><em>Department:</em></strong> Assign or create new department.</p></li><li><p><strong><em>Country:</em></strong> Location of the job.</p></li><li><p><strong><em>Job Description:</em></strong> Duties and responsibilities.</p></li><li><p><strong><em>Job Requirements:</em></strong> Skills and qualifications.</p></li><li><p><strong><em>Recruitment Target:</em></strong> Number of employees to hire.</p></li><li><p><strong><em>Employment Type:</em></strong> Select employment type.</p></li><li><p><strong><em>Status (Toggle):</em></strong> Enable/disable the job position.</p><p><img src="'+q+'" alt="General JB"></p></li></ul></li><li><p>Action Buttons:</p><ul><li>Click <strong><em>Create:</em></strong> to save the job position.</li><li>Click <strong><em>Create &amp; Create Another:</em></strong> to save and start a new job position.</li><li>Click <strong><em>Cancel</em></strong> to discard changes.</li></ul></li></ol><h2 id="summary" tabindex="-1">Summary <a class="header-anchor" href="#summary" aria-label="Permalink to &quot;Summary&quot;">​</a></h2><p>This documentation covered the creation and management of various configurations within the <strong>Employees</strong> module of <strong>AureusERP</strong>, including Activity Plans, Working Schedules, Departure Reasons, Tags, Work Locations, Skill Types, Employment Types, and Job Positions. Each configuration plays a crucial role in streamlining HR processes and enhancing employee management.</p>',38)]))}const D=o(v,[["render",T]]);export{W as __pageData,D as default};
