import{_ as o,c as t,o as i,ah as r}from"./chunks/framework.C2OYgiIb.js";const n="/images1/employees/emp_create.png",l="/images1/employees/emp_general.png",s="/images1/employees/emp_work.png",a="/images1/employees/emp_private1.png",m="/images1/employees/emp_edu.png",p="/images1/employees/emp_citi.png",g="/images1/employees/emp_workp.png",c="/images1/employees/emp_settings1.png",u="/images1/employees/emp_addinfo.png",d="/images1/employees/emp_view.png",h="/images1/employees/emp_skill.png",y="/images1/employees/emp_skill2.png",f="/images1/employees/emp_skill3.png",k="/images1/employees/emp_resu.png",b="/images1/employees/emp_resu1.png",_="/images1/employees/emp_resu2.png",x=JSON.parse('{"title":"Employees","description":"","frontmatter":{},"headers":[],"relativePath":"master/employees/employees.md","filePath":"master/employees/employees.md","lastUpdated":1763638830000}'),S={name:"master/employees/employees.md"};function E(P,e,v,w,C,A){return i(),t("div",null,e[0]||(e[0]=[r('<h1 id="employees" tabindex="-1">Employees <a class="header-anchor" href="#employees" aria-label="Permalink to &quot;Employees&quot;">​</a></h1><p>The <strong>Employees</strong> module in <strong>AureusERP</strong> allows businesses to create and manage employee records efficiently. Each employee profile includes personal, professional, and organizational details to support smooth HR operations.</p><h2 id="employee-creation" tabindex="-1">Employee Creation <a class="header-anchor" href="#employee-creation" aria-label="Permalink to &quot;Employee Creation&quot;">​</a></h2><h3 id="use-case" tabindex="-1">Use Case <a class="header-anchor" href="#use-case" aria-label="Permalink to &quot;Use Case&quot;">​</a></h3><p>This feature is used to onboard new employees into the system, manage their roles, contact details, work location, approvals, and maintain essential personal and work-related records.</p><h3 id="steps-to-create-a-new-employee" tabindex="-1">Steps to Create a New Employee <a class="header-anchor" href="#steps-to-create-a-new-employee" aria-label="Permalink to &quot;Steps to Create a New Employee&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Employees → Employees → New Employee</code></strong> as shown below.</p><p><img src="'+n+'" alt="Employee create"></p></li><li><p>Fill in the following fields:</p><h3 id="basic-details" tabindex="-1">Basic Details <a class="header-anchor" href="#basic-details" aria-label="Permalink to &quot;Basic Details&quot;">​</a></h3><ul><li><strong><em>Name:</em></strong> Full name of the employee.</li><li><strong><em>Job Title:</em></strong> Role or designation (e.g., Software Engineer, HR Manager).</li><li><strong><em>Work Email:</em></strong> Official email address used for internal communication.</li><li><strong><em>Department:</em></strong> Select the department where the employee works.</li><li><strong><em>Work Mobile:</em></strong> Mobile number used for business communication.</li><li><strong><em>Job Position:</em></strong> Link to a predefined job position created in configuration.</li><li><strong><em>Work Phone:</em></strong> Office phone or landline.</li><li><strong><em>Manager:</em></strong> Reporting manager or supervisor.</li><li><strong><em>Employee Tags:</em></strong> Add labels for easy categorization (e.g., Remote, Contract).</li><li><strong><em>Coach:</em></strong> A mentor or coach assigned to the employee.</li></ul><p><img src="'+l+'" alt="Employee create"></p><h3 id="work-information" tabindex="-1">Work Information <a class="header-anchor" href="#work-information" aria-label="Permalink to &quot;Work Information&quot;">​</a></h3><ul><li><p><strong>Location Subsection</strong></p><ul><li><strong><em>Work Address:</em></strong> Office address for the employee.</li><li><strong><em>Work Location:</em></strong> Specific office branch, home, or other.</li></ul></li><li><p><strong>Organization Details Subsection</strong></p><ul><li><strong><em>Company:</em></strong> Company name (used for multi-company setups).</li><li><strong><em>Color:</em></strong> Assign a color for visual identification (optional).</li></ul></li><li><p><strong>Approver Subsection</strong></p><ul><li><strong><em>Time Off:</em></strong> Person responsible for approving leave.</li><li><strong><em>Attendance Manager:</em></strong> Manager who monitors attendance.</li></ul></li><li><p><strong>Schedule Subsection</strong></p><ul><li><strong><em>Working Hours:</em></strong> Select from predefined working schedules.</li><li><strong><em>Time Zone:</em></strong> Time zone applicable to the employee&#39;s work location.</li></ul></li></ul><p><img src="'+s+'" alt="Employee create"></p><h3 id="private-information" tabindex="-1">Private Information <a class="header-anchor" href="#private-information" aria-label="Permalink to &quot;Private Information&quot;">​</a></h3><ul><li><p><strong>Private Contact Subsection</strong></p><ul><li><strong><em>Street 1 / Street 2 / City / Postal Code:</em></strong> Complete home address.</li><li><strong><em>Country / State:</em></strong> Residence location.</li><li><strong><em>Private Phone:</em></strong> Personal phone number.</li><li><strong><em>Bank Account:</em></strong> Bank account details for salary.</li><li><strong><em>Private Email:</em></strong> Personal email address.</li><li><strong><em>Private Car Plate:</em></strong> Vehicle registration.</li><li><strong><em>Distance Home to Work:</em></strong> Commute distance.</li><li><strong><em>Km Home to Work:</em></strong> Numeric value of the commute distance.</li><li><strong><em>Distance Unit:</em></strong> Measurement unit (Km or m).</li></ul></li></ul><p><img src="'+a+'" alt="Employee create"></p><ul><li><p><strong>Emergency Contact Subsection</strong></p><ul><li><strong><em>Contact Name:</em></strong> Name of emergency contact person.</li><li><strong><em>Contact Phone:</em></strong> Emergency contact number.</li></ul></li><li><p><strong>Family Status Subsection</strong></p><ul><li><strong><em>Marital Status:</em></strong> Select marital status (e.g., Single, Married).</li></ul></li><li><p><strong>Education Subsection</strong></p><ul><li><strong><em>Certificate Level:</em></strong> Highest education level.</li><li><strong><em>Field of Study:</em></strong> Main academic focus (e.g., Engineering, Finance).</li><li><strong><em>School:</em></strong> Name of the institution.</li></ul></li></ul><p><img src="'+m+'" alt="Employee create"></p><ul><li><p><strong>Citizenship Subsection</strong></p><ul><li><strong><em>Country / State:</em></strong> Place of citizenship.</li><li><strong><em>Identification ID / SSN No / SIN No / Passport ID:</em></strong> Government-issued IDs.</li><li><strong><em>Gender / Date of Birth / Country of Birth:</em></strong> Personal demographics.</li></ul><p><img src="'+p+'" alt="Employee create"></p></li><li><p><strong>Work Permit Subsection</strong></p><ul><li><strong><em>Visa Number / Work Permit No:</em></strong> Relevant work authorization details.</li><li><strong><em>Visa Expiration / Work Permit Expiration Date:</em></strong> Validity dates.</li><li><strong><em>Work Permit (upload):</em></strong> Upload document proof.</li></ul><p><img src="'+g+'" alt="Employee create"></p></li></ul><h3 id="settings" tabindex="-1">Settings <a class="header-anchor" href="#settings" aria-label="Permalink to &quot;Settings&quot;">​</a></h3><p>The <strong>Settings</strong> section allows configuration of employment-related options, system login, and other administrative details.</p><ul><li><p><strong>Employment Status Subsection</strong></p><ul><li><strong><em>Active Employee:</em></strong> Enable this if the employee is currently active in the organization.</li><li><strong><em>Flexible Work Arrangement:</em></strong><br> Allow flexible work timing or remote work options for this employee.</li><li><strong><em>Fully Flexible Schedule:</em></strong> Enable this if the employee can manage working hours without fixed schedules.</li><li><strong><em>Work Permit Scheduled Activity:</em></strong> Automatically generate scheduled activities related to the employee’s work permit expiry or renewal.</li><li><strong><em>Related User:</em></strong> Assign or create a user account linked to the employee.<br> This field is used to provide login credentials to the employee by either selecting an existing user or creating a new one.</li><li><strong><em>Departure Reason:</em></strong> Select the reason for departure if the employee leaves the organization.<br> Options include: <ul><li>Fired</li><li>Retired</li><li>Resigned</li></ul></li></ul></li><li><p><strong>Attendance / Point of Sale Subsection</strong></p><ul><li><strong><em>Badge ID:</em></strong> Unique identification number for attendance tracking or POS access.</li><li><strong><em>PIN:</em></strong> Security PIN used for login in attendance or POS systems.</li></ul></li></ul><p><img src="'+c+'" alt="Employee create"></p><ul><li><p><strong>Additional Information Subsection</strong></p><ul><li><strong><em>Primary Language:</em></strong> Default language used by the employee.</li><li><strong><em>Additional Notes:</em></strong> Any additional comments or remarks.</li><li><strong><em>Notes:</em></strong> Internal notes for HR reference.</li></ul></li></ul><p><img src="'+u+'" alt="Employee create"></p></li><li><p>Action Buttons</p></li></ol><ul><li><strong><em>Create:</em></strong> Save the employee profile.</li><li><strong><em>Create &amp; Create Another:</em></strong> Save and open new form.</li><li><strong><em>Cancel:</em></strong> Exit without saving.</li></ul><h2 id="employee-view-post-creation" tabindex="-1">Employee View (Post-Creation) <a class="header-anchor" href="#employee-view-post-creation" aria-label="Permalink to &quot;Employee View (Post-Creation)&quot;">​</a></h2><p>After creation, you are redirected to the <strong>View Employee</strong> page.</p><p><img src="'+d+'" alt="Employee create"></p><h3 id="edit-employee" tabindex="-1">Edit Employee <a class="header-anchor" href="#edit-employee" aria-label="Permalink to &quot;Edit Employee&quot;">​</a></h3><p>Modify any information as needed by clicking the <strong>Edit Employee</strong> button.</p><h3 id="manage-skills" tabindex="-1">Manage Skills <a class="header-anchor" href="#manage-skills" aria-label="Permalink to &quot;Manage Skills&quot;">​</a></h3><ol><li><p>Click <strong>Manage Skills</strong>.</p></li><li><p>Click <strong>Add Skill</strong>. <img src="'+h+'" alt="Employee create"></p></li><li><p>Fill in:</p><ul><li><strong><em>Skill Type:</em></strong> Select from configured options.</li><li><strong><em>Skill:</em></strong> Specific skill (e.g., JavaScript, HR Management).</li><li><strong><em>Skill Level:</em></strong> Proficiency level. <img src="'+y+'" alt="Employee create"></li></ul></li><li><p>Click <strong>Create</strong> to add the skill.</p></li></ol><p><img src="'+f+'" alt="Employee create"></p><h3 id="manage-resumes" tabindex="-1">Manage Resumes <a class="header-anchor" href="#manage-resumes" aria-label="Permalink to &quot;Manage Resumes&quot;">​</a></h3><ol><li><p>Click <strong>Manage Resumes</strong>.</p></li><li><p>Click <strong>Add Resume</strong>. <img src="'+k+'" alt="Employee create"></p></li><li><p>Fill in:</p><ul><li><strong><em>Title:</em></strong> Resume title (e.g., Resume 2025).</li><li><strong><em>Type:</em></strong> Document type (Resume, Portfolio).</li><li><strong>Duration / Start Date / End Date</strong>: Period covered.</li><li><strong><em>Display Type:</em></strong> How it&#39;s shown.</li><li><strong><em>Description:</em></strong> Additional notes. <img src="'+b+'" alt="Employee create"></li></ul></li><li><p>Click <strong>Create</strong> to save the resume.</p></li></ol><p><img src="'+_+'" alt="Employee create"></p><h2 id="summary" tabindex="-1">Summary <a class="header-anchor" href="#summary" aria-label="Permalink to &quot;Summary&quot;">​</a></h2><p>The <strong>Employees</strong> module in <strong>AureusERP</strong> provides a comprehensive system for managing employee records. From creation to skill and resume management, it supports efficient HR processes and ensures that all necessary information is captured for effective workforce management.</p>',21)]))}const R=o(S,[["render",E]]);export{x as __pageData,R as default};
