import{_ as o,c as t,o as a,ah as i}from"./chunks/framework.C2OYgiIb.js";const n="/images1/inventory/navigation-warehouse-management.png",r="/images1/inventory/warehouse_general.png",s="/images1/inventory/warehouse_shipment.png",l="/images1/inventory/warehouse_locations.png",g="/images1/inventory/navigation-warehouse-locations.png",u="/images1/inventory/warehouse_location.png",m="/images1/inventory/warehouse_operation_types.png",c="/images1/inventory/navigation-warehouse-operation-types.png",p="/images1/inventory/warehouse_operation_type.png",h="/images1/inventory/warehouse_operation_type_settings.png",d="/images1/inventory/warehouse_routes.png",f="/images1/inventory/navigation-warehouse-routes.png",w="/images1/inventory/warehouse_route.png",y="/images1/inventory/warehouse_rules.png",v="/images1/inventory/navigation-warehouse-rules.png",b="/images1/inventory/warehouse_rule.png",C="/images1/inventory/navigation-warehouse-storage-category.png",k="/images1/inventory/warehouse_storage_category.png",N=JSON.parse('{"title":"Warehouse Management Configuration","description":"","frontmatter":{},"headers":[],"relativePath":"master/inventories/configurations/warehouse-management.md","filePath":"master/inventories/configurations/warehouse-management.md","lastUpdated":1763638830000}'),S={name:"master/inventories/configurations/warehouse-management.md"};function q(_,e,P,A,x,W){return a(),t("div",null,e[0]||(e[0]=[i('<h1 id="warehouse-management-configuration" tabindex="-1">Warehouse Management Configuration <a class="header-anchor" href="#warehouse-management-configuration" aria-label="Permalink to &quot;Warehouse Management Configuration&quot;">​</a></h1><p>Warehouse Management settings help you structure your warehouse operations, define storage locations, set product movement rules, and automate warehouse workflows.</p><h2 id="warehouses" tabindex="-1">Warehouses <a class="header-anchor" href="#warehouses" aria-label="Permalink to &quot;Warehouses&quot;">​</a></h2><p>A warehouse is a physical storage location where goods are received, stored, and dispatched.</p><p><strong>Use Case:</strong> Create warehouses when your business has multiple storage buildings or locations. Each warehouse can have its own process flow and locations.</p><h3 id="steps-create-a-new-warehouse" tabindex="-1">Steps Create a New Warehouse <a class="header-anchor" href="#steps-create-a-new-warehouse" aria-label="Permalink to &quot;Steps Create a New Warehouse&quot;">​</a></h3><ol><li><p>Naviagte to <strong><code>Inventory → Configurations → Warehouse Management → Warehouses</code></strong> as shown below:</p><p><img src="'+n+'" alt="Warehouse Management Navigation"></p></li><li><p>Fill the following fields:</p><h3 id="general-information" tabindex="-1">General Information <a class="header-anchor" href="#general-information" aria-label="Permalink to &quot;General Information&quot;">​</a></h3><ul><li><strong><em>Name:</em></strong> The full name of the warehouse (e.g., Central Warehouse)</li><li><strong><em>Short Name:</em></strong> Short identifier for the warehouse (e.g., CW)</li><li><strong><em>Company:</em></strong> Company owning this warehouse (useful in multi-company setups)</li><li><strong><em>Address:</em></strong> Physical location address of the warehouse</li></ul><p><img src="'+r+'" alt="Create Warehouse"></p><h3 id="shipment-settings" tabindex="-1">Shipment Settings <a class="header-anchor" href="#shipment-settings" aria-label="Permalink to &quot;Shipment Settings&quot;">​</a></h3><p>These define how products move in and out of the warehouse.</p><h4 id="incoming-shipments" tabindex="-1">Incoming Shipments <a class="header-anchor" href="#incoming-shipments" aria-label="Permalink to &quot;Incoming Shipments&quot;">​</a></h4><ul><li><strong><em>One Step – Receive &amp; Store:</em></strong> Goods go directly to stock in one step</li><li><strong><em>Two Steps – Receive → Store:</em></strong> Goods received at a receiving zone, then stored</li><li><strong><em>Three Steps – Receive → Quality Control → Store:</em></strong> Goods received, quality-checked, then stored</li></ul><h4 id="outgoing-shipments" tabindex="-1">Outgoing Shipments <a class="header-anchor" href="#outgoing-shipments" aria-label="Permalink to &quot;Outgoing Shipments&quot;">​</a></h4><ul><li><strong><em>One Step – Deliver:</em></strong> Deliver goods in a single step</li><li><strong><em>Two Steps – Pick → Deliver</em></strong> Pick goods first, then deliver</li><li><strong><em>Three Steps – Pick → Pack → Deliver:</em></strong> Pick, then pack, then deliver in 3 steps</li></ul><p><img src="'+s+'" alt="Create Warehouse"></p><blockquote><p><strong>Automatic Configuration:</strong></p></blockquote><blockquote><p>Based on selected shipment flows, the system automatically creates <strong>Locations, Operation Types, Routes, and Rules</strong> for the warehouse.</p></blockquote></li><li><p>Action Buttons</p><ul><li><strong><em>Create:</em></strong> Save warehouse</li><li><strong><em>Create &amp; Create Another:</em></strong> Save and add another</li><li><strong><em>Cancel:</em></strong> Exit without saving</li></ul></li></ol><h2 id="locations" tabindex="-1">Locations <a class="header-anchor" href="#locations" aria-label="Permalink to &quot;Locations&quot;">​</a></h2><p>A location represents a physical area inside a warehouse such as shelves, racks, receiving zone, or packing zone.</p><p><strong>Use Case:</strong> Create locations when you need to organize warehouse space and track stock movement accurately.</p><blockquote><p><strong>Automatic Configuration:</strong> When warehouse shipment steps are set (1,2,3 steps), default locations are auto-created. For example, in 3-step flow, system creates: <strong>Stock, Input, Quality Control, Output, Packing Zone</strong></p></blockquote><p><img src="'+l+'" alt="Warehouse Locations"></p><h3 id="steps-to-create-a-new-location" tabindex="-1">Steps to Create a New Location <a class="header-anchor" href="#steps-to-create-a-new-location" aria-label="Permalink to &quot;Steps to Create a New Location&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Inventory → Configurations → Warehouse Management → Locations</code></strong> as shown below:</p><p><img src="'+g+'" alt="Warehouse Locations Navigation"></p></li><li><p>Fill in the following fields:</p><h3 id="general-information-1" tabindex="-1">General Information <a class="header-anchor" href="#general-information-1" aria-label="Permalink to &quot;General Information&quot;">​</a></h3><ul><li><strong><em>Location Name:</em></strong> Name of the location (e.g., Shelf A, QC Zone)</li><li><strong><em>Parent Location:</em></strong> Select if this location is inside another location (like aisle inside warehouse)</li><li><strong><em>External Notes:</em></strong> Notes for warehouse team handling instructions etc.</li></ul><h3 id="location-settings" tabindex="-1">Location Settings <a class="header-anchor" href="#location-settings" aria-label="Permalink to &quot;Location Settings&quot;">​</a></h3><ul><li><strong><em>Location Type:</em></strong> Defines stock movement behavior <ul><li>Vendor Location: Goods received from vendor</li><li>View: Logical grouping/no stock stored</li><li>Internal Location: Areas inside warehouse (main storage)</li><li>Customer Location: Location where items are shipped/sold</li><li>Inventory Loss: Location used to adjust stock differences</li><li>Production: For manufacturing WIP and finished goods</li><li>Transit Location: For goods in transit between warehouses</li></ul></li><li><strong><em>Company:</em></strong> Assign company</li><li><strong><em>Is Dock Location:</em></strong> Marks as loading/unloading area</li></ul></li></ol><p><img src="'+u+'" alt="Create Location"></p><ol start="3"><li><p>Action Buttons</p><ul><li><strong><em>Create:</em></strong> Save location</li><li><strong><em>Create &amp; Create Another:</em></strong> Save and add another</li><li><strong><em>Cancel:</em></strong> Exit without saving</li></ul></li></ol><h2 id="operation-types" tabindex="-1">Operation Types <a class="header-anchor" href="#operation-types" aria-label="Permalink to &quot;Operation Types&quot;">​</a></h2><p>An operation type controls how stock moves in the warehouse, including receiving, delivery, picking, and other movements.</p><p><strong>Use Case:</strong> Create operation types when you need to customize workflows for receipts, delivery, internal transfers, or dropshipping.</p><blockquote><p><strong>Automatic Creation:</strong> Operation types are auto-created when warehouse shipment flow is set. Example (3-step): Receipts, Pick, Pack, Delivery, Quality, Storage, Internal Transfer, Cross-dock</p></blockquote><p><img src="'+m+'" alt="Warehouse Operation Types"></p><h3 id="steps-to-create-a-new-operation-type" tabindex="-1">Steps to Create a New Operation Type <a class="header-anchor" href="#steps-to-create-a-new-operation-type" aria-label="Permalink to &quot;Steps to Create a New Operation Type&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Inventory → Configurations → Warehouse Management → Operation Types</code></strong> as shown below:</p><p><img src="'+c+'" alt="Warehouse Operation Types Navigation"></p></li><li><p>Fill in the following fields:</p><h3 id="basic-information" tabindex="-1">Basic Information <a class="header-anchor" href="#basic-information" aria-label="Permalink to &quot;Basic Information&quot;">​</a></h3><ul><li><strong><em>Operation Name:</em></strong> Name of the operation type</li><li><strong><em>Operation Type:</em></strong> Select purpose (Receipt, Delivery, Internal, Dropship)</li><li><strong><em>Company:</em></strong> Assigned company</li><li><strong><em>Sequence Prefix:</em></strong> Prefix for operation number (e.g., REC-001)</li><li><strong><em>Return Type:</em></strong> Defines how returns move</li><li><strong><em>Warehouse:</em></strong> Select warehouse</li><li><strong><em>Create Backorder:</em></strong> Handle partial delivery (Ask/Always/Never)</li></ul><p><img src="'+p+'" alt="Create Operation Type"></p><h3 id="reservation-settings" tabindex="-1">Reservation Settings <a class="header-anchor" href="#reservation-settings" aria-label="Permalink to &quot;Reservation Settings&quot;">​</a></h3><ul><li><strong><em>Reservation Method:</em></strong><ul><li>At Confirm: Reserve stock when order is confirmed</li><li>Manual: User manually reserves</li><li>Before Scheduled Date: Auto-reserve before shipment date</li></ul></li></ul><h3 id="additional-configuration" tabindex="-1">Additional Configuration <a class="header-anchor" href="#additional-configuration" aria-label="Permalink to &quot;Additional Configuration&quot;">​</a></h3><ul><li><p><strong><em>Lots/Serial Settings:</em></strong></p><ul><li>Create New: Create new lot/serial during operation</li><li>Use Existing: Use existing lot/serial numbers</li></ul></li><li><p><strong><em>Location Settings:</em></strong></p><ul><li>Source Location: Where goods come from</li><li>Destination Location: Where goods go</li></ul></li></ul></li></ol><p><img src="'+h+'" alt="Create Operation Type"></p><ol start="3"><li><p>Action Buttons</p><ul><li><strong><em>Create:</em></strong> Save operation type</li><li><strong><em>Create &amp; Create Another:</em></strong> Save and add another</li><li><strong><em>Cancel:</em></strong> Exit without saving</li></ul></li></ol><h2 id="routes" tabindex="-1">Routes <a class="header-anchor" href="#routes" aria-label="Permalink to &quot;Routes&quot;">​</a></h2><p>A route defines the product movement rules inside a warehouse, determining how goods travel between locations.</p><p><strong>Use Case:</strong> Create routes when you need to control how products travel across locations (pick-pack-ship, cross-dock etc.)</p><blockquote><p><strong>Automatic Generation:</strong> Routes are auto-generated based on warehouse setup. Example routes:</p></blockquote><blockquote><ul><li>Receive in 3 steps</li><li>Deliver in 3 steps</li><li>Cross-dock</li></ul></blockquote><p><img src="'+d+'" alt="Warehouse Routes"></p><h3 id="steps-to-create-a-route" tabindex="-1">Steps to Create a Route <a class="header-anchor" href="#steps-to-create-a-route" aria-label="Permalink to &quot;Steps to Create a Route&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Inventory → Configurations → Warehouse Management → Routes</code></strong> as shown below:</p><p><img src="'+f+'" alt="Warehouse Routes Navigation"></p></li><li><p>Fill in the following fields:</p><h3 id="basic-information-1" tabindex="-1">Basic Information <a class="header-anchor" href="#basic-information-1" aria-label="Permalink to &quot;Basic Information&quot;">​</a></h3><ul><li><strong><em>Route:</em></strong> Name of route</li><li><strong><em>Company:</em></strong> Assign company</li></ul><h3 id="applicable-on-settings" tabindex="-1">Applicable On Settings <a class="header-anchor" href="#applicable-on-settings" aria-label="Permalink to &quot;Applicable On Settings&quot;">​</a></h3><ul><li><p><strong><em>Available For:</em></strong></p><ul><li>Product Categories: Route available for product categories</li><li>Products: Route available for products</li><li>Packaging: Route available for packaging</li><li>Warehouses: Select warehouse to make default route</li></ul><p><img src="'+w+'" alt="Create Route"></p></li></ul></li><li><p>Action Buttons</p><ul><li><strong><em>Create:</em></strong> Save route</li><li><strong><em>Create &amp; Create Another:</em></strong> Save and add another</li><li><strong><em>Cancel:</em></strong> Exit without saving</li></ul></li></ol><h2 id="rules" tabindex="-1">Rules <a class="header-anchor" href="#rules" aria-label="Permalink to &quot;Rules&quot;">​</a></h2><p>A rule defines how products move within a route, automating warehouse operations and movements.</p><p><strong>Use Case:</strong> Create rules when you need to automate actions like pull from supplier, push to packing, or move to QC.</p><blockquote><p><strong>Automatic Generation:</strong> Three actions are auto-generated:</p></blockquote><blockquote><ul><li>Pull From</li><li>Push To</li><li>Buy</li></ul></blockquote><p><img src="'+y+'" alt="Warehouse Rules"></p><h3 id="steps-to-create-a-rule" tabindex="-1">Steps to Create a Rule <a class="header-anchor" href="#steps-to-create-a-rule" aria-label="Permalink to &quot;Steps to Create a Rule&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Inventory → Configurations → Warehouse Management → Rules</code></strong> as shown below:</p><p><img src="'+v+'" alt="Warehouse Rules Navigation"></p></li><li><p>Fill in the following fields:</p><h3 id="basic-information-2" tabindex="-1">Basic Information <a class="header-anchor" href="#basic-information-2" aria-label="Permalink to &quot;Basic Information&quot;">​</a></h3><ul><li><strong><em>Name:</em></strong> Name of rule</li><li><strong><em>Action:</em></strong> Select type (Pull, Push, Buy, Pull &amp; Push)</li><li><strong><em>Operation Type:</em></strong> Select operation type</li><li><strong><em>Source Location:</em></strong> From where goods move</li><li><strong><em>Destination Location:</em></strong> Where goods go</li></ul><h3 id="movement-settings" tabindex="-1">Movement Settings <a class="header-anchor" href="#movement-settings" aria-label="Permalink to &quot;Movement Settings&quot;">​</a></h3><ul><li><strong><em>Partner Address:</em></strong> Vendor/customer address</li><li><strong><em>Lead Time:</em></strong> Days required for movement</li><li><strong><em>Route:</em></strong> Assign route</li><li><strong><em>Company:</em></strong> Assign company</li></ul><p><img src="'+b+'" alt="Create Rule"></p></li><li><p>Action Buttons</p><ul><li><strong><em>Create:</em></strong> Save rule</li><li><strong><em>Create &amp; Create Another:</em></strong> Save and add another</li><li><strong><em>Cancel:</em></strong> Exit without saving</li></ul></li></ol><h2 id="storage-categories" tabindex="-1">Storage Categories <a class="header-anchor" href="#storage-categories" aria-label="Permalink to &quot;Storage Categories&quot;">​</a></h2><p>A storage category helps define storage capacity rules per location, allowing you to manage space and weight limitations effectively.</p><p><strong>Use Case:</strong> Create storage categories when you need to limit product storage by weight or quantity (e.g., shelf has 100kg capacity).</p><h3 id="steps-to-create-a-storage-category" tabindex="-1">Steps to Create a Storage Category <a class="header-anchor" href="#steps-to-create-a-storage-category" aria-label="Permalink to &quot;Steps to Create a Storage Category&quot;">​</a></h3><ol><li><p>Navigate to <strong><code>Inventory → Configurations → Warehouse Management → Storage Category</code></strong> as shown below:</p><p><img src="'+C+'" alt="Warehouse Storage Category Navigation"></p></li><li><p>Fill in the following fields:</p><h3 id="basic-information-3" tabindex="-1">Basic Information <a class="header-anchor" href="#basic-information-3" aria-label="Permalink to &quot;Basic Information&quot;">​</a></h3><ul><li><strong><em>Name:</em></strong> Category name (e.g., Heavy Goods Zone)</li><li><strong><em>Max Weight:</em></strong> Maximum allowed weight</li><li><strong><em>Allow New Products:</em></strong> Storage type rules: <ul><li>Mixed: Any products allowed</li><li>Empty: Only empty slots allowed</li><li>Same: Only same product allowed</li></ul></li><li><strong><em>Company:</em></strong> Assign company</li></ul><h3 id="additional-configuration-options" tabindex="-1">Additional Configuration Options <a class="header-anchor" href="#additional-configuration-options" aria-label="Permalink to &quot;Additional Configuration Options&quot;">​</a></h3><ul><li><p><strong><em>Capacity Management:</em></strong></p><ul><li>Capacity by Package: Set capacity by packaging type</li><li>Capacity by Product: Set capacity by product quantity</li><li>Locations: Link storage category to warehouse locations</li></ul><p><img src="'+k+'" alt="Create Storage Category"></p></li></ul></li><li><p>Action Buttons</p><ul><li><strong><em>Create:</em></strong> Save storage category</li><li><strong><em>Create &amp; Create Another:</em></strong> Save and add another</li><li><strong><em>Cancel:</em></strong> Exit without saving</li></ul></li></ol><h3 id="summary" tabindex="-1">Summary <a class="header-anchor" href="#summary" aria-label="Permalink to &quot;Summary&quot;">​</a></h3><p>The <strong>Warehouse Management</strong> section in Inventory helps to:</p><ul><li>Configure warehouses &amp; their shipment process</li><li>Auto-generate warehouse structures (locations, operations, routes, rules)</li><li>Create &amp; manage custom warehouse locations</li><li>Define stock movement rules &amp; automation</li><li>Set capacity &amp; product storage categories</li></ul><p><strong>In simple words:</strong><br> You set up how your warehouse works, how products move in/out, where products are stored, and how operations are managed ensuring accurate and efficient inventory flow.</p>',50)]))}const L=o(S,[["render",q]]);export{N as __pageData,L as default};
